import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { inventoryAPI } from '../api';
import { useAuth } from '../hooks';

function PartsListPage() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [parts, setParts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [lowStockOnly, setLowStockOnly] = useState(false);

  // Par√ßacƒ± (parts_dealer) rol√º kontrol√º
  useEffect(() => {
    if (user && user.role !== 'parts_dealer') {
      // Par√ßacƒ± deƒüilse ana sayfaya y√∂nlendir
      navigate('/');
    }
  }, [user, navigate]);

  useEffect(() => {
    fetchData();
  }, [selectedCategory, lowStockOnly]);

  const fetchData = async () => {
    try {
      setIsLoading(true);
      const [partsRes, categoriesRes] = await Promise.all([
        inventoryAPI.getParts({ 
          category: selectedCategory || undefined,
          ...(lowStockOnly && { low_stock: true })
        }),
        inventoryAPI.getCategories()
      ]);
      setParts(partsRes.data.results || partsRes.data || []);
      setCategories(categoriesRes.data.results || categoriesRes.data || []);
    } catch (error) {
      console.error('Veri y√ºkleme hatasƒ±:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      fetchData();
      return;
    }

    try {
      setIsLoading(true);
      const response = await inventoryAPI.search(searchQuery);
      setParts(response.data.results || []);
    } catch (error) {
      console.error('Arama hatasƒ±:', error);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-orange-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">Par√ßa Y√∂netimi</h1>
          <p className="text-gray-600">Stoklarƒ±nƒ±zƒ± y√∂netin ve takip edin</p>
        </div>

        {/* Arama ve Filtreler */}
        <div className="bg-white rounded-2xl shadow-lg p-6 mb-6 border border-gray-100">
          {/* Google benzeri arama */}
          <form onSubmit={handleSearch} className="mb-4">
            <div className="flex gap-2">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Par√ßa adƒ±, kod, ara√ß, kategori veya notlarda ara..."
                className="flex-1 px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
              />
              <button
                type="submit"
                className="bg-orange-600 hover:bg-orange-700 text-white font-bold px-6 py-3 rounded-lg transition"
              >
                üîç Ara
              </button>
            </div>
          </form>

          {/* Filtreler */}
          <div className="flex flex-wrap gap-4">
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 outline-none"
            >
              <option value="">T√ºm Kategoriler</option>
              {categories.map(cat => (
                <option key={cat.id} value={cat.name}>
                  {cat.name}{cat.subcategory ? ` > ${cat.subcategory}` : ''}
                </option>
              ))}
            </select>

            <label className="flex items-center gap-2 cursor-pointer">
              <input
                type="checkbox"
                checked={lowStockOnly}
                onChange={(e) => setLowStockOnly(e.target.checked)}
                className="w-5 h-5 text-orange-600 rounded focus:ring-orange-500"
              />
              <span className="text-gray-700">Sadece D√º≈ü√ºk Stok</span>
            </label>

            <Link
              to="/parts/new"
              className="ml-auto bg-green-600 hover:bg-green-700 text-white font-bold px-4 py-2 rounded-lg transition"
            >
              ‚ûï Yeni Par√ßa Ekle
            </Link>
          </div>
        </div>

        {/* Par√ßa Listesi */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {parts.length === 0 ? (
            <div className="col-span-full text-center py-12">
              <p className="text-gray-500 text-lg">Hen√ºz par√ßa eklenmemi≈ü.</p>
              <Link
                to="/parts/new"
                className="mt-4 inline-block bg-orange-600 hover:bg-orange-700 text-white font-bold px-6 py-3 rounded-lg transition"
              >
                ƒ∞lk Par√ßayƒ± Ekle
              </Link>
            </div>
          ) : (
            parts.map(part => (
              <Link
                key={part.id}
                to={`/parts/${part.id}`}
                className="bg-white rounded-xl shadow-md hover:shadow-xl transition p-6 border border-gray-100"
              >
                <div className="flex items-start justify-between mb-3">
                  <div className="flex-1">
                    <h3 className="text-lg font-bold text-gray-900 mb-1">{part.name}</h3>
                    <p className="text-sm text-gray-500">
                      {part.category_name}
                      {part.category_subcategory && ` > ${part.category_subcategory}`}
                    </p>
                  </div>
                  {part.is_low_stock && (
                    <span className="bg-red-100 text-red-700 px-2 py-1 rounded text-xs font-bold">
                      ‚ö†Ô∏è D√º≈ü√ºk
                    </span>
                  )}
                </div>

                <div className="space-y-2 text-sm">
                  {part.code && (
                    <div className="flex items-center gap-2 text-gray-600">
                      <span className="font-medium">Kod:</span>
                      <span>{part.code}</span>
                    </div>
                  )}
                  {part.auto_code && (
                    <div className="flex items-center gap-2 text-gray-500 text-xs">
                      <span>Otomatik Kod:</span>
                      <span>{part.auto_code}</span>
                    </div>
                  )}
                  {part.brand && (
                    <div className="flex items-center gap-2 text-gray-600">
                      <span className="font-medium">Marka:</span>
                      <span>{part.brand}</span>
                    </div>
                  )}
                  <div className="flex items-center gap-2">
                    <span className="font-medium text-gray-700">Stok:</span>
                    <span className={`font-bold ${part.is_low_stock ? 'text-red-600' : 'text-green-600'}`}>
                      {part.quantity} adet
                    </span>
                  </div>
                  {part.vehicle_count > 0 && (
                    <div className="text-gray-500 text-xs">
                      {part.vehicle_count} araca uyumlu
                    </div>
                  )}
                  {part.current_sale_price && (
                    <div className="text-orange-600 font-bold">
                      {parseFloat(part.current_sale_price).toFixed(2)} TL
                    </div>
                  )}
                </div>
              </Link>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default PartsListPage;
