import React, { createContext, useState, useEffect } from 'react';
import { usersAPI } from './api';

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [token, setToken] = useState(localStorage.getItem('access_token'));

  useEffect(() => {
    // Token check is not needed for session-based auth
    setIsLoading(false);
  }, []);

  const login = async (username, password) => {
    try {
      const response = await usersAPI.login(username, password);
      // Backend token yerine user bilgisini dönüyor, session kullancağız
      setUser(response.data);
      localStorage.setItem('user_id', response.data.id);
      localStorage.setItem('username', response.data.username);
      return true;
    } catch (error) {
      console.error('Giriş hatası:', error.response?.data || error.message);
      return false;
    }
  };

  const register = async (data) => {
    try {
      const response = await usersAPI.register(data);
      // Kaydolduktan hemen sonra login yap
      setUser(response.data);
      localStorage.setItem('user_id', response.data.id);
      localStorage.setItem('username', response.data.username);
      return true;
    } catch (error) {
      console.error('Kayıt hatası:', error.response?.data || error.message);
      return false;
    }
  };

  const logout = () => {
    localStorage.removeItem('access_token');
    setToken(null);
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, token, isLoading, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
