import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { messagingAPI } from '../api';
import { useAuth } from '../hooks';

function MessagingPage() {
  const { conversationId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSending, setIsSending] = useState(false);
  const [error, setError] = useState('');
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Load conversations on mount
  useEffect(() => {
    if (user) {
      fetchConversations();
    }
  }, [user]);

  // Load specific conversation if conversationId is provided
  useEffect(() => {
    if (conversationId && conversations.length > 0) {
      const conv = conversations.find(c => c.id === parseInt(conversationId));
      if (conv) {
        selectConversation(conv);
      }
    }
  }, [conversationId, conversations]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const fetchConversations = async () => {
    try {
      setIsLoading(true);
      const response = await messagingAPI.getConversations();
      setConversations(response.data.results || response.data || []);
      
      // If conversationId is provided, select that conversation
      if (conversationId) {
        const conv = (response.data.results || response.data || []).find(
          c => c.id === parseInt(conversationId)
        );
        if (conv) {
          selectConversation(conv);
        }
      } else if ((response.data.results || response.data || []).length > 0) {
        // Select first conversation if no specific ID
        selectConversation((response.data.results || response.data || [])[0]);
      }
    } catch (error) {
      console.error('Sohbetler yÃ¼klenemedi:', error);
      setError('Sohbetler yÃ¼klenirken hata oluÅŸtu.');
    } finally {
      setIsLoading(false);
    }
  };

  const selectConversation = async (conversation) => {
    try {
      setSelectedConversation(conversation);
      const response = await messagingAPI.getConversation(conversation.id);
      setMessages(response.data.messages || []);
      // Mark messages as read
      await messagingAPI.markAsRead(conversation.id);
      // Update URL
      navigate(`/messages/${conversation.id}`, { replace: true });
    } catch (error) {
      console.error('Sohbet yÃ¼klenemedi:', error);
      setError('Sohbet yÃ¼klenirken hata oluÅŸtu.');
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !selectedConversation) return;

    try {
      setIsSending(true);
