import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { inventoryAPI } from '../api';
import { useAuth } from '../hooks';

function PartDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  const [part, setPart] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('info');
  const [newNote, setNewNote] = useState('');
  const [movementData, setMovementData] = useState({
    movement_type: 'purchase',
    quantity: '',
    note: '',
  });
  const [purchaseData, setPurchaseData] = useState({
    price: '',
    quantity: '',
    supplier: '',
    note: '',
  });
  const [saleData, setSaleData] = useState({
    price: '',
    quantity: '',
    customer: '',
    note: '',
  });

  useEffect(() => {
    fetchPart();
  }, [id]);

  const fetchPart = async () => {
    try {
      setIsLoading(true);
      const response = await inventoryAPI.getPart(id);
      setPart(response.data);
    } catch (error) {
      console.error('Par√ßa y√ºkleme hatasƒ±:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddNote = async () => {
    if (!newNote.trim()) return;

    try {
      await inventoryAPI.addNoteToPart(id, newNote);
      setNewNote('');
      fetchPart();
    } catch (error) {
      console.error('Not ekleme hatasƒ±:', error);
      alert('Not eklenemedi.');
    }
  };

  const handleStockMovement = async (e) => {
    e.preventDefault();
    if (!movementData.quantity) return;

    try {
      await inventoryAPI.stockMovement(id, movementData);
      setMovementData({ movement_type: 'purchase', quantity: '', note: '' });
      fetchPart();
      alert('Stok hareketi kaydedildi!');
    } catch (error) {
      console.error('Stok hareketi hatasƒ±:', error);
      alert(error.response?.data?.error || 'Stok hareketi kaydedilemedi.');
    }
  };

  const handleAddPurchasePrice = async (e) => {
    e.preventDefault();
    if (!purchaseData.price) return;

    try {
      await inventoryAPI.addPurchasePrice(id, purchaseData);
      setPurchaseData({ price: '', quantity: '', supplier: '', note: '' });
      fetchPart();
      alert('Alƒ±≈ü fiyatƒ± eklendi!');
    } catch (error) {
      console.error('Alƒ±≈ü fiyatƒ± hatasƒ±:', error);
      alert('Alƒ±≈ü fiyatƒ± eklenemedi.');
    }
  };

  const handleAddSalePrice = async (e) => {
    e.preventDefault();
    if (!saleData.price) return;

    try {
      await inventoryAPI.addSalePrice(id, saleData);
      setSaleData({ price: '', quantity: '', customer: '', note: '' });
      fetchPart();
      alert('Satƒ±≈ü fiyatƒ± eklendi!');
    } catch (error) {
      console.error('Satƒ±≈ü fiyatƒ± hatasƒ±:', error);
      alert('Satƒ±≈ü fiyatƒ± eklenemedi.');
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-orange-500"></div>
      </div>
    );
  }

  if (!part) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Par√ßa Bulunamadƒ±</h2>
          <Link to="/parts" className="text-orange-600 hover:underline">
            Par√ßa listesine d√∂n
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-6">
          <Link
            to="/parts"
            className="text-orange-600 hover:text-orange-700 font-medium mb-2 inline-block"
          >
            ‚Üê Par√ßa Listesine D√∂n
          </Link>
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 mb-2">{part.name}</h1>
              <p className="text-gray-600">
                {part.category_name}
                {part.category_subcategory && ` > ${part.category_subcategory}`}
              </p>
            </div>
            <Link
              to={`/parts/${id}/edit`}
              className="bg-orange-600 hover:bg-orange-700 text-white font-bold px-6 py-3 rounded-lg transition"
            >
              ‚úèÔ∏è D√ºzenle
            </Link>
          </div>
        </div>

        {/* Stok Durumu Kartƒ± */}
        <div className="bg-white rounded-2xl shadow-lg p-6 mb-6 border border-gray-100">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="text-center p-4 bg-blue-50 rounded-lg">
              <div className="text-2xl font-bold text-blue-600">{part.quantity}</div>
              <div className="text-sm text-gray-600">Mevcut Stok</div>
            </div>
            <div className="text-center p-4 bg-orange-50 rounded-lg">
              <div className="text-2xl font-bold text-orange-600">
                {part.current_purchase_price ? `${parseFloat(part.current_purchase_price).toFixed(2)} TL` : '-'}
              </div>
              <div className="text-sm text-gray-600">Alƒ±≈ü Fiyatƒ±</div>
            </div>
            <div className="text-center p-4 bg-green-50 rounded-lg">
              <div className="text-2xl font-bold text-green-600">
                {part.current_sale_price ? `${parseFloat(part.current_sale_price).toFixed(2)} TL` : '-'}
              </div>
              <div className="text-sm text-gray-600">Satƒ±≈ü Fiyatƒ±</div>
            </div>
            <div className="text-center p-4 bg-purple-50 rounded-lg">
              <div className="text-2xl font-bold text-purple-600">
                {part.profit_percentage ? `${parseFloat(part.profit_percentage).toFixed(1)}%` : '-'}
              </div>
              <div className="text-sm text-gray-600">K√¢r Y√ºzdesi</div>
            </div>
          </div>
          {part.is_low_stock && (
            <div className="mt-4 bg-red-50 text-red-700 p-3 rounded-lg border border-red-200 text-center font-bold">
              ‚ö†Ô∏è D√º≈ü√ºk Stok Uyarƒ±sƒ±! (Minimum: {part.min_stock})
            </div>
          )}
        </div>

        {/* Tabs */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-100">
          <div className="border-b border-gray-200">
            <nav className="flex -mb-px">
              {[
                { id: 'info', label: 'üìã Bilgiler' },
                { id: 'vehicles', label: 'üöó Uyumlu Ara√ßlar' },
                { id: 'alternatives', label: 'üîÑ Alternatifler' },
                { id: 'notes', label: 'üìù Notlar' },
                { id: 'prices', label: 'üí∞ Fiyat Ge√ßmi≈üi' },
                { id: 'movements', label: 'üì¶ Stok Hareketleri' },
              ].map(tab => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`px-6 py-4 font-medium text-sm border-b-2 transition ${
                    activeTab === tab.id
                      ? 'border-orange-500 text-orange-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          <div className="p-6">
            {/* Bilgiler Tab */}
            {activeTab === 'info' && (
              <div className="space-y-4">
                {part.image && (
                  <div>
                    <img
                      src={part.image}
                      alt={part.name}
                      className="max-w-md rounded-lg shadow-md"
                    />
                  </div>
                )}
                <div className="grid grid-cols-2 gap-4">
                  {part.brand && (
                    <div>
                      <span className="text-sm font-semibold text-gray-600">Marka:</span>
                      <p className="text-lg">{part.brand}</p>
                    </div>
                  )}
                  {part.code && (
                    <div>
                      <span className="text-sm font-semibold text-gray-600">Par√ßa Kodu:</span>
                      <p className="text-lg">{part.code}</p>
                    </div>
                  )}
                  {part.auto_code && (
                    <div>
                      <span className="text-sm font-semibold text-gray-600">Otomatik Kod:</span>
                      <p className="text-lg font-mono">{part.auto_code}</p>
                    </div>
                  )}
                  <div>
                    <span className="text-sm font-semibold text-gray-600">Minimum Stok:</span>
                    <p className="text-lg">{part.min_stock}</p>
                  </div>
                </div>
              </div>
            )}

            {/* Uyumlu Ara√ßlar Tab */}
            {activeTab === 'vehicles' && (
              <div>
                {part.compatibilities && part.compatibilities.length > 0 ? (
                  <div className="space-y-2">
                    {part.compatibilities.map(comp => (
                      <div
                        key={comp.id}
                        className="p-4 bg-blue-50 rounded-lg border border-blue-200"
                      >
                        <div className="font-bold text-blue-900">
                          {comp.vehicle_details.brand} {comp.vehicle_details.model}
                        </div>
                        <div className="text-sm text-blue-700">
                          {comp.vehicle_details.year_from}
                          {comp.vehicle_details.year_to && `-${comp.vehicle_details.year_to}`}
                          {comp.vehicle_details.engine && ` ‚Ä¢ ${comp.vehicle_details.engine}`}
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-500">Hen√ºz uyumlu ara√ß eklenmemi≈ü.</p>
                )}
              </div>
            )}

            {/* Alternatifler Tab */}
            {activeTab === 'alternatives' && (
              <div>
                {part.alternatives && part.alternatives.length > 0 ? (
                  <div className="space-y-2">
                    {part.alternatives.map(alt => (
                      <div
                        key={alt.id}
                        className="p-4 bg-green-50 rounded-lg border border-green-200"
                      >
                        <div className="font-bold text-green-900">{alt.alternative_details.name}</div>
                        <div className="text-sm text-green-700">
                          {alt.alternative_details.category}
                          {alt.alternative_details.code && ` ‚Ä¢ Kod: ${alt.alternative_details.code}`}
                        </div>
                        {alt.note && (
                          <div className="mt-2 text-sm text-gray-600 italic">"{alt.note}"</div>
                        )}
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-500">Hen√ºz alternatif par√ßa eklenmemi≈ü.</p>
                )}
              </div>
            )}

            {/* Notlar Tab */}
            {activeTab === 'notes' && (
              <div className="space-y-4">
                {/* Not Ekleme Formu */}
                <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                  <h3 className="font-bold text-gray-900 mb-3">Yeni Not Ekle</h3>
                  <textarea
                    value={newNote}
                    onChange={(e) => setNewNote(e.target.value)}
                    placeholder="√ñrn: Bu muadil Astra J'de ses yapƒ±yor..."
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none mb-3 min-h-[100px]"
                  />
                  <button
                    onClick={handleAddNote}
                    className="bg-orange-600 hover:bg-orange-700 text-white font-bold px-6 py-2 rounded-lg transition"
                  >
                    Not Ekle
                  </button>
                </div>

                {/* Notlar Listesi */}
                {part.notes && part.notes.length > 0 ? (
                  <div className="space-y-3">
                    {part.notes.map(note => (
                      <div
                        key={note.id}
                        className="p-4 bg-yellow-50 rounded-lg border border-yellow-200"
                      >
                        <div className="flex justify-between items-start mb-2">
                          <div className="font-semibold text-gray-900">
                            {note.created_by_name || note.created_by_username || 'Anonim'}
                          </div>
                          <div className="text-sm text-gray-500">
                            {new Date(note.created_at).toLocaleDateString('tr-TR')}
                          </div>
                        </div>
                        <p className="text-gray-700">{note.note}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-500">Hen√ºz not eklenmemi≈ü.</p>
                )}
              </div>
            )}

            {/* Fiyat Ge√ßmi≈üi Tab */}
            {activeTab === 'prices' && (
              <div className="space-y-6">
                {/* Alƒ±≈ü Fiyatƒ± Ekleme */}
                <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
                  <h3 className="font-bold text-blue-900 mb-3">Alƒ±≈ü Fiyatƒ± Ekle</h3>
                  <form onSubmit={handleAddPurchasePrice} className="space-y-3">
                    <div className="grid grid-cols-2 gap-3">
                      <input
                        type="number"
                        placeholder="Fiyat (TL)"
                        value={purchaseData.price}
                        onChange={(e) => setPurchaseData({ ...purchaseData, price: e.target.value })}
                        step="0.01"
                        className="px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 outline-none"
                        required
                      />
                      <input
                        type="number"
                        placeholder="Miktar"
                        value={purchaseData.quantity}
                        onChange={(e) => setPurchaseData({ ...purchaseData, quantity: e.target.value })}
                        className="px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 outline-none"
                      />
                    </div>
                    <input
                      type="text"
                      placeholder="Tedarik√ßi"
                      value={purchaseData.supplier}
                      onChange={(e) => setPurchaseData({ ...purchaseData, supplier: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 outline-none"
                    />
                    <textarea
                      placeholder="Not"
                      value={purchaseData.note}
                      onChange={(e) => setPurchaseData({ ...purchaseData, note: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 outline-none"
                    />
                    <button
                      type="submit"
                      className="bg-blue-600 hover:bg-blue-700 text-white font-bold px-6 py-2 rounded-lg transition"
                    >
                      Alƒ±≈ü Fiyatƒ± Ekle
                    </button>
                  </form>
                </div>

                {/* Satƒ±≈ü Fiyatƒ± Ekleme */}
                <div className="bg-green-50 p-4 rounded-lg border border-green-200">
                  <h3 className="font-bold text-green-900 mb-3">Satƒ±≈ü Fiyatƒ± Ekle</h3>
                  <form onSubmit={handleAddSalePrice} className="space-y-3">
                    <div className="grid grid-cols-2 gap-3">
                      <input
                        type="number"
                        placeholder="Fiyat (TL)"
                        value={saleData.price}
                        onChange={(e) => setSaleData({ ...saleData, price: e.target.value })}
                        step="0.01"
                        className="px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-green-500 outline-none"
                        required
                      />
                      <input
                        type="number"
                        placeholder="Miktar"
                        value={saleData.quantity}
                        onChange={(e) => setSaleData({ ...saleData, quantity: e.target.value })}
                        className="px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-green-500 outline-none"
                      />
                    </div>
                    <input
                      type="text"
                      placeholder="M√º≈üteri"
                      value={saleData.customer}
                      onChange={(e) => setSaleData({ ...saleData, customer: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-green-500 outline-none"
                    />
                    <textarea
                      placeholder="Not"
                      value={saleData.note}
                      onChange={(e) => setSaleData({ ...saleData, note: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-green-500 outline-none"
                    />
                    <button
                      type="submit"
                      className="bg-green-600 hover:bg-green-700 text-white font-bold px-6 py-2 rounded-lg transition"
                    >
                      Satƒ±≈ü Fiyatƒ± Ekle
                    </button>
                  </form>
                </div>

                {/* Fiyat Ge√ßmi≈üi */}
                <div className="space-y-4">
                  <h3 className="font-bold text-gray-900">Alƒ±≈ü Fiyat Ge√ßmi≈üi</h3>
                  {part.purchase_prices && part.purchase_prices.length > 0 ? (
                    <div className="space-y-2">
                      {part.purchase_prices.map(price => (
                        <div
                          key={price.id}
                          className="p-3 bg-blue-50 rounded-lg border border-blue-200 flex justify-between items-center"
                        >
                          <div>
                            <div className="font-bold text-blue-900">{price.price} TL</div>
                            <div className="text-sm text-blue-700">
                              {price.quantity} adet ‚Ä¢ {price.supplier && `Tedarik√ßi: ${price.supplier}`}
                            </div>
                            <div className="text-xs text-gray-500">
                              {price.created_by_name} ‚Ä¢ {new Date(price.created_at).toLocaleDateString('tr-TR')}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-gray-500">Hen√ºz alƒ±≈ü fiyatƒ± kaydƒ± yok.</p>
                  )}

                  <h3 className="font-bold text-gray-900 mt-6">Satƒ±≈ü Fiyat Ge√ßmi≈üi</h3>
                  {part.sale_prices && part.sale_prices.length > 0 ? (
                    <div className="space-y-2">
                      {part.sale_prices.map(price => (
                        <div
                          key={price.id}
                          className="p-3 bg-green-50 rounded-lg border border-green-200 flex justify-between items-center"
                        >
                          <div>
                            <div className="font-bold text-green-900">{price.price} TL</div>
                            <div className="text-sm text-green-700">
                              {price.quantity} adet ‚Ä¢ {price.customer && `M√º≈üteri: ${price.customer}`}
                            </div>
                            <div className="text-xs text-gray-500">
                              {price.created_by_name} ‚Ä¢ {new Date(price.created_at).toLocaleDateString('tr-TR')}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-gray-500">Hen√ºz satƒ±≈ü fiyatƒ± kaydƒ± yok.</p>
                  )}
                </div>
              </div>
            )}

            {/* Stok Hareketleri Tab */}
            {activeTab === 'movements' && (
              <div className="space-y-4">
                {/* Stok Hareketi Ekleme */}
                <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                  <h3 className="font-bold text-gray-900 mb-3">Stok Hareketi Ekle</h3>
                  <form onSubmit={handleStockMovement} className="space-y-3">
                    <select
                      value={movementData.movement_type}
                      onChange={(e) => setMovementData({ ...movementData, movement_type: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 outline-none"
                    >
                      <option value="purchase">Alƒ±≈ü</option>
                      <option value="sale">Satƒ±≈ü</option>
                      <option value="return">ƒ∞ade</option>
                      <option value="adjustment">Manuel D√ºzeltme</option>
                    </select>
                    <input
                      type="number"
                      placeholder="Miktar"
                      value={movementData.quantity}
                      onChange={(e) => setMovementData({ ...movementData, quantity: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 outline-none"
                      required
                    />
                    <textarea
                      placeholder="Not"
                      value={movementData.note}
                      onChange={(e) => setMovementData({ ...movementData, note: e.target.value })}
                      className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 outline-none"
                    />
                    <button
                      type="submit"
                      className="bg-orange-600 hover:bg-orange-700 text-white font-bold px-6 py-2 rounded-lg transition"
                    >
                      Hareket Ekle
                    </button>
                  </form>
                </div>

                {/* Stok Hareketleri Listesi */}
                {part.movements && part.movements.length > 0 ? (
                  <div className="space-y-2">
                    {part.movements.map(movement => (
                      <div
                        key={movement.id}
                        className="p-4 bg-gray-50 rounded-lg border border-gray-200"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <div className="font-bold text-gray-900">
                              {movement.movement_type === 'purchase' && 'üì• Alƒ±≈ü'}
                              {movement.movement_type === 'sale' && 'üì§ Satƒ±≈ü'}
                              {movement.movement_type === 'return' && '‚Ü©Ô∏è ƒ∞ade'}
                              {movement.movement_type === 'adjustment' && '‚úèÔ∏è Manuel D√ºzeltme'}
                            </div>
                            <div className="text-sm text-gray-600">
                              Miktar: {movement.quantity}
                            </div>
                            {movement.note && (
                              <div className="text-sm text-gray-500 mt-1 italic">"{movement.note}"</div>
                            )}
                          </div>
                          <div className="text-xs text-gray-500">
                            {movement.created_by_name} ‚Ä¢ {new Date(movement.created_at).toLocaleDateString('tr-TR')}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-500">Hen√ºz stok hareketi kaydƒ± yok.</p>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default PartDetailPage;
