import React, { createContext, useState, useEffect } from 'react';
import { usersAPI } from './api';

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Page yüklendiğinde user bilgisini localStorage'dan geri yükle
    const userId = localStorage.getItem('user_id');
    const username = localStorage.getItem('username');
    if (userId && username) {
      setUser({ id: parseInt(userId), username: username });
    }
    
    // CSRF token'ı her zaman al (başlangıçta ve login sonrasında)
    fetch('http://localhost:8000/api/csrf-token/')
      .then(res => res.json())
      .then(data => {
        if (data.csrfToken) {
          localStorage.setItem('csrfToken', data.csrfToken);
        }
      })
      .catch(err => console.warn('CSRF token alınamadı:', err));
    
    setIsLoading(false);
  }, []);

  const login = async (username, password) => {
    try {
      // Önce CSRF token al
      const csrfResponse = await fetch('http://localhost:8000/api/csrf-token/');
      const csrfData = await csrfResponse.json();
      if (csrfData.csrfToken) {
        localStorage.setItem('csrfToken', csrfData.csrfToken);
      }
      
      // Sonra login yap
      const response = await usersAPI.login(username, password);
      setUser(response.data);
      localStorage.setItem('user_id', response.data.id);
      localStorage.setItem('username', response.data.username);
      
      // Login sonrası yeni CSRF token al
      const csrfResponse2 = await fetch('http://localhost:8000/api/csrf-token/');
      const csrfData2 = await csrfResponse2.json();
      if (csrfData2.csrfToken) {
        localStorage.setItem('csrfToken', csrfData2.csrfToken);
      }
      
      return true;
    } catch (error) {
      console.error('Login error:', error.response?.data || error.message);
      return false;
    }
  };

  const register = async (data) => {
    try {
      const response = await usersAPI.register(data);
      // Kaydolduktan hemen sonra login yap
      setUser(response.data);
      localStorage.setItem('user_id', response.data.id);
      localStorage.setItem('username', response.data.username);
      return true;
    } catch (error) {
      console.error('Kayıt hatası:', error.response?.data || error.message);
      return false;
    }
  };

  const logout = () => {
    // Session-based auth olduğu için localStorage'ı temizle
    localStorage.removeItem('user_id');
    localStorage.removeItem('username');
    localStorage.removeItem('access_token');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, isLoading, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
