                <p className="text-2xl mb-2">üîç</p>
                <p className="text-gray-600 text-lg">Arama kriterlerine uygun d√ºkk√¢n bulunamadƒ±.</p>
                <p className="text-gray-500 text-sm mt-2">Filtrelerinizi deƒüi≈ütirmeyi deneyin.</p>
              </div>
            ) : (
              <div className="space-y-4">
                {workshops.map((ws) => (
                  <Link
                    key={ws.id}
                    to={`/workshops/${ws.id}`}
                    className={`bg-gradient-to-br ${getCategoryColor(ws.category_name)} rounded-2xl shadow-md hover:shadow-xl transition-all duration-300 overflow-hidden border group`}
                  >
                    <div className="flex gap-5 p-5">
                      {/* Resim Alanƒ± */}
                      <div className="w-32 h-32 flex-shrink-0">
                        {ws.image ? (
                          <img
                            src={ws.image}
                            alt={ws.name}
                            className="w-full h-full object-cover rounded-xl group-hover:scale-105 transition-transform duration-300 shadow-md"
                          />
                        ) : (
                          <div className={`w-full h-full bg-gradient-to-br ${getCategoryColor(ws.category_name)} rounded-xl flex items-center justify-center text-4xl shadow-md`}>
                            {getCategoryIcon(ws.category_name)}
                          </div>
                        )}
                      </div>

                      {/* ƒ∞√ßerik */}
                      <div className="flex-1 flex flex-col justify-between">
                        <div>
