import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks';

function ProfilePage() {
  const { user, isLoading, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isLoading && !user) {
      navigate('/login');
    }
  }, [user, isLoading, navigate]);

  if (isLoading) {
    return <div className="text-center py-8">Yükleniyor...</div>;
  }

  if (!user) {
    return null;
  }

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="max-w-2xl mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow p-8">
        <h1 className="text-3xl font-bold mb-6">Profilim</h1>

        <div className="space-y-4">
          <div>
            <label className="text-gray-600">Ad</label>
            <p className="text-lg font-semibold text-gray-800">{user.first_name || '-'} {user.last_name || '-'}</p>
          </div>

          <div>
            <label className="text-gray-600">Kullanıcı Adı</label>
            <p className="text-lg font-semibold text-gray-800">{user.username}</p>
          </div>

          <div>
            <label className="text-gray-600">E-posta</label>
            <p className="text-lg font-semibold text-gray-800">{user.email || '-'}</p>
          </div>

          <div>
            <label className="text-gray-600">Telefon</label>
            <p className="text-lg font-semibold text-gray-800">{user.phone || '-'}</p>
          </div>

          <div>
            <label className="text-gray-600">Rol</label>
            <p className="text-lg font-semibold text-gray-800">
              {user.role === 'customer' ? 'Müşteri' : user.role === 'workshop' ? 'Dükkân Sahibi' : 'Parçacı'}
            </p>
          </div>

          {user.is_premium && (
            <div className="mt-4 p-4 bg-orange-50 rounded-lg border border-orange-200">
              <p className="text-orange-800 font-semibold">✓ Premium Üyesi</p>
            </div>
          )}
        </div>

        <button
          onClick={handleLogout}
          className="mt-8 w-full bg-red-600 text-white py-2 rounded-lg font-bold hover:bg-red-700"
        >
          Çıkış Yap
        </button>
      </div>
    </div>
  );
}

export default ProfilePage;
