import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { inventoryAPI } from '../api';
import { useAuth } from '../hooks';

function PartFormPage() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    brand: '',
    year: '',
    code: '',
    quantity: '',
    min_stock: '1',
    current_purchase_price: '',
    current_sale_price: '',
    image: null,
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [categoriesRes, brandsRes] = await Promise.all([
        inventoryAPI.getCategories(),
        inventoryAPI.getBrands(),
      ]);
      setCategories(categoriesRes.data.results || categoriesRes.data || []);
      setBrands(brandsRes.data.brands || []);
    } catch (error) {
      console.error('Veri yÃ¼kleme hatasÄ±:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData({ ...formData, image: files[0] || null });
    } else {
      setFormData({ ...formData, [name]: value });
    }
    // Hata temizle
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});

    // Validasyon
    const newErrors = {};
    if (!formData.name.trim()) {
      newErrors.name = 'ParÃ§a adÄ± zorunludur';
    }
    if (!formData.category) {
      newErrors.category = 'Kategori seÃ§imi zorunludur';
    }
    if (!formData.brand) {
      newErrors.brand = 'Marka seÃ§imi zorunludur';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsLoading(true);

    try {
      const submitData = new FormData();
      submitData.append('name', formData.name);
      submitData.append('category', formData.category);
      submitData.append('brand', formData.brand);
      if (formData.year) submitData.append('year', formData.year);
      if (formData.code) submitData.append('code', formData.code);
      if (formData.quantity) submitData.append('quantity', formData.quantity);
      if (formData.min_stock) submitData.append('min_stock', formData.min_stock);
      if (formData.current_purchase_price) submitData.append('current_purchase_price', formData.current_purchase_price);
      if (formData.current_sale_price) submitData.append('current_sale_price', formData.current_sale_price);
      if (formData.image) submitData.append('image', formData.image);

      await inventoryAPI.createPart(submitData);
      navigate('/parts');
    } catch (error) {
      console.error('ParÃ§a ekleme hatasÄ±:', error);
      if (error.response?.data) {
        setErrors(error.response.data);
      } else {
        setErrors({ general: 'ParÃ§a eklenirken bir hata oluÅŸtu.' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  // MarkalarÄ± gruplara ayÄ±r
  const brandGroups = [];
  let currentGroup = null;
  
  brands.forEach(([value, label]) => {
    // EÄŸer label emoji iÃ§eriyorsa, bu bir grup baÅŸlÄ±ÄŸÄ±dÄ±r
    if (label.match(/ðŸ‡¹ðŸ‡·|ðŸ‡©ðŸ‡ª|ðŸ‡«ðŸ‡·|ðŸ‡¯ðŸ‡µ|ðŸ‡°ðŸ‡·|ðŸ‡¸ðŸ‡ª|ðŸ‡ºðŸ‡¸|ðŸ‡¨ðŸ‡³|ðŸ‡¬ðŸ‡§/)) {
      if (currentGroup) {
        brandGroups.push(currentGroup);
      }
      currentGroup = { header: label, options: [] };
    } else if (currentGroup) {
      currentGroup.options.push({ value, label });
    }
  });
  if (currentGroup) {
    brandGroups.push(currentGroup);
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
