import { useAuth } from '../hooks';

function WorkshopDetailPage() {
  const { id } = useParams();
  const { user } = useAuth();
  const [workshop, setWorkshop] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [reviewError, setReviewError] = useState('');
  const [reviewSuccess, setReviewSuccess] = useState('');
  const [newReview, setNewReview] = useState({ rating: 5, comment: '' });

  // Kategori renklerini belirle
  const getCategoryGradient = (categoryName) => {
    const gradients = {
      'Elektrik√ßi': 'from-yellow-400 to-yellow-600',
      'Tesisat√ßƒ±': 'from-blue-400 to-blue-600',
      'Marangoz': 'from-amber-400 to-amber-600',
      'Boyacƒ±': 'from-red-400 to-red-600',
      'Cam Ustasƒ±': 'from-cyan-400 to-cyan-600',
      'Asans√∂rc√º': 'from-indigo-400 to-indigo-600',
      'Kaloriferci': 'from-orange-400 to-orange-600',
    };
    return gradients[categoryName] || 'from-gray-400 to-gray-600';
  };

  const getCategoryIcon = (categoryName) => {
    const icons = {
      'Elektrik√ßi': '‚ö°',
      'Tesisat√ßƒ±': 'üîß',
      'Marangoz': 'ü™µ',
      'Boyacƒ±': 'üé®',
      'Cam Ustasƒ±': 'ü™ü',
      'Asans√∂rc√º': 'üè¢',
      'Kaloriferci': 'üî•',
    };
    return icons[categoryName] || 'üè™';
  };

  const fetchData = async () => {
    try {
      setIsLoading(true);
      const [workshopRes, reviewsRes] = await Promise.all([
        workshopsAPI.getWorkshop(id),
        reviewsAPI.getReviews(id)
      ]);
      setWorkshop(workshopRes.data);
      setReviews(reviewsRes.data.results || reviewsRes.data || []);
    } catch (error) {
      console.error('Veri y√ºkleme hatasƒ±:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const handleSubmitReview = async () => {
    setReviewError('');
    setReviewSuccess('');

    if (!newReview.comment.trim()) {
      setReviewError('L√ºtfen bir yorum yazƒ±nƒ±z.');
      return;
    }

    try {
      setIsSubmitting(true);
      await reviewsAPI.createReview(id, newReview);
      setNewReview({ rating: 5, comment: '' });
      setReviewSuccess('Yorumunuz ba≈üarƒ±yla g√∂nderildi!');
      await fetchData();
      setTimeout(() => setReviewSuccess(''), 3000);
    } catch (error) {
      console.error('Yorum g√∂nderme hatasƒ±:', error);
      setReviewError(error.response?.data?.detail || 'Yorum g√∂nderirken hata olu≈ütu.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleStartConversation = async () => {
    if (!user) {
      setReviewError('Mesaj g√∂ndermek i√ßin giri≈ü yapmalƒ±sƒ±nƒ±z.');
      return;
    }

    try {
      setIsSubmitting(true);
      await messagingAPI.startConversation({ workshop_id: id });
      setReviewSuccess('Sohbet ba≈ülatƒ±ldƒ±! Mesajlarƒ± g√∂rmek i√ßin profil sayfanƒ±za gidin.');
      setTimeout(() => setReviewSuccess(''), 3000);
    } catch (error) {
      console.error('Sohbet ba≈ülatma hatasƒ±:', error);
      setReviewError(error.response?.data?.detail || 'Sohbet ba≈ülatƒ±rken hata olu≈ütu.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return <div className="text-center py-8 text-gray-600">Y√ºkleniyor...</div>;
  }

  if (!workshop) {
    return <div className="text-center py-8 text-gray-600">D√ºkk√¢n bulunamadƒ±.</div>;
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      {/* D√ºkk√¢n Ba≈ülƒ±ƒüƒ± - Renkli */}
      <div className={`bg-gradient-to-r ${getCategoryGradient(workshop.category_name)} rounded-3xl shadow-xl p-8 mb-8 text-white`}>
        <div className="flex gap-8 items-start">
          {workshop.image ? (
            <img src={workshop.image} alt={workshop.name} className="w-48 h-48 object-cover rounded-2xl shadow-lg border-4 border-white" />
          ) : (
            <div className="w-48 h-48 bg-white bg-opacity-20 rounded-2xl flex items-center justify-center text-6xl shadow-lg border-4 border-white">
              {getCategoryIcon(workshop.category_name)}
            </div>
          )}
          <div className="flex-1">
            <div className="flex items-start justify-between mb-4">
              <div>
                <h1 className="text-4xl font-bold mb-2">{workshop.name}</h1>
                <p className="text-white text-opacity-90 text-lg mb-1">üìå {workshop.category_name}</p>
                <p className="text-white text-opacity-80">üìç {workshop.district}</p>
              </div>
              {workshop.is_premium && (
                <div className="bg-yellow-300 text-yellow-900 px-6 py-2 rounded-full font-bold flex items-center gap-2 shadow-lg">
                  ‚≠ê PREMIUM
                </div>
              )}
            </div>

            <div className="mt-6 grid grid-cols-2 gap-4">
              <div className="bg-white bg-opacity-20 rounded-lg p-3">
                <p className="text-sm text-white text-opacity-80">Ortalama Puan</p>
                <p className="text-2xl font-bold">‚≠ê {workshop.average_rating.toFixed(1)}</p>
              </div>
              <div className="bg-white bg-opacity-20 rounded-lg p-3">
                <p className="text-sm text-white text-opacity-80">Toplam Yorum</p>
                <p className="text-2xl font-bold">üí¨ {workshop.total_reviews}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* ƒ∞leti≈üim Kartlarƒ± */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-8">
        <div className="bg-gradient-to-br from-blue-50 to-blue-100 rounded-2xl p-6 border-2 border-blue-200">
          <p className="text-sm text-blue-600 font-semibold mb-2">üìû Telefon</p>
          <p className="text-xl font-bold text-blue-900">{workshop.phone}</p>
        </div>
        {workshop.whatsapp && (
          <div className="bg-gradient-to-br from-green-50 to-green-100 rounded-2xl p-6 border-2 border-green-200">
            <p className="text-sm text-green-600 font-semibold mb-2">üí¨ WhatsApp</p>
            <p className="text-xl font-bold text-green-900">{workshop.whatsapp}</p>
          </div>
        )}
        {workshop.email && (
          <div className="bg-gradient-to-br from-purple-50 to-purple-100 rounded-2xl p-6 border-2 border-purple-200">
            <p className="text-sm text-purple-600 font-semibold mb-2">üìß Email</p>
            <p className="text-xl font-bold text-purple-900">{workshop.email}</p>
          </div>
        )}
        <div className="bg-gradient-to-br from-orange-50 to-orange-100 rounded-2xl p-6 border-2 border-orange-200">
          <button
            onClick={handleStartConversation}
            disabled={isSubmitting}
            className="w-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold py-3 rounded-lg hover:shadow-lg transition disabled:opacity-50"
          >
            üí¨ Mesaj G√∂nder
          </button>
        </div>
      </div>

      {/* A√ßƒ±klama */}
      <div className="bg-white rounded-2xl shadow-lg p-8 mb-8 border-l-4 border-blue-500">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">Hakkƒ±nda</h2>
        <p className="text-gray-700 leading-relaxed">{workshop.description}</p>
      </div>

      {/* Yorumlar Ba≈ülƒ±ƒüƒ± */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-600 rounded-2xl shadow-lg p-6 mb-8 text-white">
        <h2 className="text-3xl font-bold">üí¨ M√º≈üteri Yorumlarƒ±</h2>
        <p className="text-indigo-100 mt-1">{reviews.length} yorum</p>
      </div>

      {/* Yorum Yazma Formu */}
      {user ? (
        <div className="bg-gradient-to-br from-green-50 to-green-100 rounded-2xl shadow-lg p-8 mb-8 border-2 border-green-200">
          <h3 className="text-2xl font-bold text-gray-800 mb-6">Siz de Yorum Yazƒ±n</h3>
          
          {reviewError && (
            <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg mb-4">
              {reviewError}
            </div>
          )}

          {reviewSuccess && (
            <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 rounded-lg mb-4">
              {reviewSuccess}
            </div>
          )}

          <div className="space-y-4">
            {/* Puan Se√ßimi */}
            <div>
              <label className="block text-lg font-semibold text-gray-700 mb-3">Puan Verin</label>
              <div className="flex gap-3">
                {[1, 2, 3, 4, 5].map(star => (
                  <button
                    key={star}
                    onClick={() => setNewReview({ ...newReview, rating: star })}
                    className={`text-4xl transition ${
                      star <= newReview.rating ? 'text-yellow-400' : 'text-gray-300'
                    } hover:text-yellow-400`}
                  >
                    ‚òÖ
                  </button>
                ))}
              </div>
            </div>

            {/* Yorum Yazƒ± */}
            <div>
              <label className="block text-lg font-semibold text-gray-700 mb-3">Yorum</label>
              <textarea
                value={newReview.comment}
                onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                placeholder="Deneyiminizi payla≈üƒ±n..."
                rows="5"
                className="w-full px-4 py-3 border-2 border-green-300 rounded-lg focus:outline-none focus:border-green-500 focus:ring-2 focus:ring-green-200 resize-none"
              />
            </div>

            <button
              onClick={handleSubmitReview}
              disabled={isSubmitting}
              className="w-full bg-gradient-to-r from-green-500 to-emerald-600 text-white font-bold py-3 rounded-lg hover:shadow-lg transition disabled:opacity-50"
            >
              {isSubmitting ? 'G√∂nderiliyor...' : '‚úì Yorumu G√∂nder'}
            </button>
          </div>
        </div>
      ) : (
        <div className="bg-blue-50 border-2 border-blue-200 rounded-2xl p-8 mb-8 text-center">
          <p className="text-gray-700 mb-4">Yorum yazmak i√ßin l√ºtfen <strong>giri≈ü yapƒ±n</strong>.</p>
        </div>
      )}

      {/* Yorumlar Listesi */}
      <div className="space-y-4">
        {reviews.length === 0 ? (
          <div className="bg-gray-50 rounded-2xl p-8 text-center text-gray-600">
            <p className="text-lg">Hen√ºz yorum yapƒ±lmamƒ±≈ü. ƒ∞lk yorumu siz yazƒ±n! üëá</p>
          </div>
        ) : (
          reviews.map(review => (
            <div key={review.id} className="bg-gradient-to-br from-purple-50 to-pink-50 rounded-2xl p-6 border-l-4 border-purple-400">
              <div className="flex justify-between items-start mb-3">
                <div>
                  <p className="font-bold text-gray-800">{review.user_name || 'Anonim'}</p>
                  <div className="flex text-yellow-400 text-sm">
                    {[...Array(5)].map((_, i) => (
                      <span key={i}>{i < review.rating ? '‚òÖ' : '‚òÜ'}</span>
                    ))}
                  </div>
                </div>
                <span className="text-xs text-gray-500">{new Date(review.created_at).toLocaleDateString('tr-TR')}</span>
              </div>
              <p className="text-gray-700">{review.comment}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default WorkshopDetailPage;
