import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { workshopsAPI, reviewsAPI, messagingAPI } from '../api';
import { useAuth } from '../hooks';

function WorkshopDetailPage() {
  const { id } = useParams();
  const { user } = useAuth();
  const [workshop, setWorkshop] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [reviewError, setReviewError] = useState('');
  const [reviewSuccess, setReviewSuccess] = useState('');
  const [newReview, setNewReview] = useState({ rating: 5, comment: '' });

  const fetchData = async () => {
    try {
      setIsLoading(true);
      const [workshopRes, reviewsRes] = await Promise.all([
        workshopsAPI.getWorkshop(id),
        reviewsAPI.getReviews(id)
      ]);
      setWorkshop(workshopRes.data);
      setReviews(reviewsRes.data.results || reviewsRes.data || []);
    } catch (error) {
      console.error('Veri yükleme hatası:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const handleSubmitReview = async () => {
    setReviewError('');
    setReviewSuccess('');

    if (!newReview.comment.trim()) {
      setReviewError('Lütfen bir yorum yazınız.');
      return;
    }

    try {
      setIsSubmitting(true);
      await reviewsAPI.createReview(id, newReview);
      setNewReview({ rating: 5, comment: '' });
      setReviewSuccess('Yorumunuz başarıyla gönderildi!');
      await fetchData();
      setTimeout(() => setReviewSuccess(''), 3000);
    } catch (error) {
      console.error('Yorum gönderme hatası:', error);
      setReviewError(error.response?.data?.detail || 'Yorum gönderirken hata oluştu.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleStartConversation = async () => {
    if (!user) {
      setReviewError('Mesaj göndermek için giriş yapmalısınız.');
      return;
    }

    try {
      setIsSubmitting(true);
      await messagingAPI.startConversation({ workshop_id: id });
      setReviewSuccess('Sohbet başlatıldı! Mesajları görmek için profil sayfanıza gidin.');
      setTimeout(() => setReviewSuccess(''), 3000);
    } catch (error) {
      console.error('Sohbet başlatma hatası:', error);
      setReviewError(error.response?.data?.detail || 'Sohbet başlatırken hata oluştu.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return <div className="text-center py-8">Yükleniyor...</div>;
  }

  if (!workshop) {
    return <div className="text-center py-8">Dükkân bulunamadı.</div>;
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      {/* Dükkân Başlığı */}
      <div className="bg-white rounded-lg shadow p-6 mb-6">
        <div className="flex gap-6 mb-6">
          {workshop.image && (
            <img src={workshop.image} alt={workshop.name} className="w-48 h-48 object-cover rounded-lg" />
          )}
          <div className="flex-1">
            <h1 className="text-3xl font-bold text-gray-800 mb-2">{workshop.name}</h1>
            <p className="text-gray-600 mb-4">{workshop.category_name}</p>
            
            <div className="flex items-center gap-4 mb-4">
              <span className="bg-yellow-100 text-yellow-800 px-3 py-1 rounded-full text-lg font-bold">
                ★ {workshop.average_rating}
              </span>
              <span className="text-gray-600">{workshop.total_reviews} yorum</span>
              {workshop.is_premium && (
                <span className="bg-orange-100 text-orange-800 px-3 py-1 rounded-full text-sm font-bold">
                  PREMIUM ÜYE
                </span>
              )}
            </div>

            <div className="space-y-2 mb-6">
              <p className="text-gray-700"><strong>Adres:</strong> {workshop.address}</p>
              <p className="text-gray-700"><strong>Telefon:</strong> {workshop.phone}</p>
              {workshop.whatsapp && (
                <p className="text-gray-700"><strong>WhatsApp:</strong> {workshop.whatsapp}</p>
              )}
              <p className="text-gray-700"><strong>İlçe:</strong> {workshop.district}</p>
            </div>

            <div className="flex gap-3">
              <a href={`tel:${workshop.phone}`} className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700">
                Ara
              </a>
              {workshop.whatsapp && (
                <a href={`https://wa.me/${workshop.whatsapp}`} target="_blank" rel="noopener noreferrer"
                  className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700">
                  WhatsApp
                </a>
              )}
              <button onClick={handleStartConversation}
                disabled={isSubmitting}
                className="bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700 disabled:opacity-50">
                {isSubmitting ? 'Gönderiliyor...' : 'Mesaj Gönder'}
              </button>
            </div>
          </div>
        </div>

        {/* Açıklama */}
        {workshop.description && (
          <div className="mt-6 pt-6 border-t">
            <h2 className="text-xl font-bold mb-3">Hakkında</h2>
            <p className="text-gray-700">{workshop.description}</p>
          </div>
        )}

        {/* Hizmetler */}
        {workshop.services && workshop.services.length > 0 && (
          <div className="mt-6 pt-6 border-t">
            <h2 className="text-xl font-bold mb-3">Hizmetler</h2>
            <ul className="space-y-2">
              {workshop.services.map(service => (
                <li key={service.id} className="text-gray-700">
                  <strong>{service.name}</strong> {service.price && `- ${service.price}₺`}
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Çalışma Saatleri */}
        {workshop.working_hours && workshop.working_hours.length > 0 && (
          <div className="mt-6 pt-6 border-t">
            <h2 className="text-xl font-bold mb-3">Çalışma Saatleri</h2>
            <ul className="space-y-1">
              {workshop.working_hours.map(hour => (
                <li key={hour.id} className="text-gray-700">
                  <strong>{hour.day_display}:</strong> {hour.is_closed ? 'Kapalı' : `${hour.opening_time} - ${hour.closing_time}`}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      {/* Yorumlar */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-2xl font-bold mb-6">Yorumlar ({reviews.length})</h2>

        {/* Yorum Formu */}
        {user && (
          <div className="mb-6 p-4 bg-gray-50 rounded-lg">
            <h3 className="font-bold mb-4">Yorum Yazın</h3>
            
            {reviewError && (
              <div className="mb-4 p-3 bg-red-100 text-red-800 rounded-lg text-sm">
                {reviewError}
              </div>
            )}
            
            {reviewSuccess && (
              <div className="mb-4 p-3 bg-green-100 text-green-800 rounded-lg text-sm">
                {reviewSuccess}
              </div>
            )}
            
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-2">Puan</label>
                <select
                  value={newReview.rating}
                  onChange={(e) => setNewReview({...newReview, rating: parseInt(e.target.value)})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  disabled={isSubmitting}
                >
                  {[1, 2, 3, 4, 5].map(r => (
                    <option key={r} value={r}>{r} Yıldız</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Yorum</label>
                <textarea
                  value={newReview.comment}
                  onChange={(e) => setNewReview({...newReview, comment: e.target.value})}
                  placeholder="Deneyiminizi paylaşın..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  rows="3"
                  disabled={isSubmitting}
                />
              </div>
              <button
                onClick={handleSubmitReview}
                disabled={isSubmitting}
                className="bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700 disabled:opacity-50"
              >
                {isSubmitting ? 'Gönderiliyor...' : 'Yorum Gönder'}
              </button>
            </div>
          </div>
        )}

        {/* Yorum Listesi */}
        <div className="space-y-4">
          {reviews.map(review => (
            <div key={review.id} className="p-4 border-l-4 border-yellow-400">
              <div className="flex justify-between items-start mb-2">
                <strong className="text-gray-800">{review.user_name}</strong>
                <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded text-sm">
                  {'★'.repeat(review.rating)}
                </span>
              </div>
              <p className="text-gray-700 mb-2">{review.comment}</p>
              <p className="text-xs text-gray-500">{new Date(review.created_at).toLocaleDateString('tr-TR')}</p>
              
              {review.reply && (
                <div className="mt-3 ml-4 p-3 bg-gray-50 rounded border-l-2 border-blue-400">
                  <p className="text-sm font-semibold text-blue-700 mb-1">Dükkân Sahibinin Yanıtı:</p>
                  <p className="text-gray-700">{review.reply.comment}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default WorkshopDetailPage;
