import React, { useState, useEffect, useCallback } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { workshopsAPI } from '../api';

function WorkshopListPage() {
  const [workshops, setWorkshops] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchParams] = useSearchParams();
  
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    district: '',
    min_rating: '',
    search: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [categoriesRes, workshopsRes] = await Promise.all([
          workshopsAPI.getCategories(),
          workshopsAPI.getWorkshops(filters)
        ]);
        setCategories(categoriesRes.data.results || categoriesRes.data || []);
        setWorkshops(workshopsRes.data.results || workshopsRes.data || []);
      } catch (error) {
        console.error('Veri yÃ¼kleme hatasÄ±:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchData();
  }, [filters]);

  const handleFilterChange = useCallback((e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  }, []);

  const StarRating = ({ rating, reviews }) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={i <= Math.floor(rating) ? 'text-yellow-400' : 'text-gray-300'}>
          â˜…
        </span>
      );
    }
    return (
      <div className="flex items-center gap-2">
        <div className="flex text-sm">{stars}</div>
        <span className="text-xs text-gray-600 font-medium">{rating.toFixed(1)}</span>
        <span className="text-xs text-gray-500">({reviews})</span>
      </div>
    );
  };

  // Kategori renklerini belirle
  const getCategoryColor = (categoryName) => {
    const colors = {
      'ElektrikÃ§i': 'from-yellow-100 to-yellow-50 border-yellow-200',
      'TesisatÃ§Ä±': 'from-blue-100 to-blue-50 border-blue-200',
      'Marangoz': 'from-amber-100 to-amber-50 border-amber-200',
      'BoyacÄ±': 'from-red-100 to-red-50 border-red-200',
      'Cam UstasÄ±': 'from-cyan-100 to-cyan-50 border-cyan-200',
      'AsansÃ¶rcÃ¼': 'from-indigo-100 to-indigo-50 border-indigo-200',
      'Kaloriferci': 'from-orange-100 to-orange-50 border-orange-200',
      'Cam UstasÄ±': 'from-slate-100 to-slate-50 border-slate-200',
    };
    return colors[categoryName] || 'from-gray-100 to-gray-50 border-gray-200';
  };

  // Kategori ikonu belirle
  const getCategoryIcon = (categoryName) => {
    const icons = {
      'ElektrikÃ§i': 'âš¡',
      'TesisatÃ§Ä±': 'ğŸ”§',
      'Marangoz': 'ğŸªµ',
      'BoyacÄ±': 'ğŸ¨',
      'Cam UstasÄ±': 'ğŸªŸ',
      'AsansÃ¶rcÃ¼': 'ğŸ¢',
      'Kaloriferci': 'ğŸ”¥',
    };
    return icons[categoryName] || 'ğŸª';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Hero AlanÄ± */}
      <div className="bg-gradient-to-r from-orange-500 via-orange-600 to-red-600 text-white py-12">
        <div className="w-full px-4">
          <div className="max-w-full">
            <h1 className="text-5xl font-bold mb-3">DÃ¼kkanlarÄ± KeÅŸfet</h1>
            <p className="text-xl text-orange-100 mb-8">En iyi dÃ¼kkanlarÄ± keÅŸfet, iÅŸÃ§ileri puanla ve gÃ¶zden kaÃ§Ä±rmayacak hizmetler al</p>
            
            {/* Hero Arama AlanÄ± */}
            <div className="flex gap-3">
              <input
                type="text"
                name="search"
                value={filters.search}
                onChange={handleFilterChange}
                placeholder="DÃ¼kkÃ¢n, kategori veya iÅŸÃ§i adÄ± ara..."
                className="flex-1 px-6 py-3 rounded-lg text-gray-900 focus:outline-none focus:ring-4 focus:ring-orange-300 transition text-lg"
              />
              <button className="bg-white hover:bg-gray-50 text-orange-600 font-bold px-8 py-3 rounded-lg transition shadow-lg hover:shadow-xl">
                ğŸ” Ara
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Ana Ä°Ã§erik */}
      <div className="w-full px-4 py-8 relative">
        {/* Arka plan ÅŸeridi */}
        <div className="absolute right-0 top-0 bottom-0 w-1/3 bg-gradient-to-b from-gray-200 to-gray-100 opacity-40 rounded-3xl"></div>
        
        <div className="max-w-full relative z-10">
          <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
            {/* Sol: Filtre Paneli */}
            <div className="lg:col-span-2">
              <div className="sticky top-4 bg-white rounded-2xl shadow-lg p-6 border border-gray-100 h-fit">
                <h2 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-2">
                  <span className="text-2xl">ğŸ”</span> Filtrele
                </h2>
                
                <div className="space-y-6">
                  {/* Kategori */}
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-3">Kategori</label>
                    <select
                      name="category"
                      value={filters.category}
                      onChange={handleFilterChange}
                      className="w-full px-4 py-2.5 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent transition text-sm appearance-none bg-white cursor-pointer"
                    >
                      <option value="">TÃ¼m Kategoriler</option>
                      {categories.map(cat => (
                        <option key={cat.id} value={cat.id}>{cat.name}</option>
                      ))}
                    </select>
                  </div>

                  {/* Ä°lÃ§e */}
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-3">Ä°lÃ§e</label>
                    <input
                      type="text"
                      name="district"
                      value={filters.district}
                      onChange={handleFilterChange}
                      placeholder="Ä°lÃ§e adÄ±..."
                      className="w-full px-4 py-2.5 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent transition text-sm"
                    />
                  </div>

                  {/* Minimum Puan */}
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-3">Minimum Puan</label>
                    <div className="space-y-2">
                      {[
                        { value: '', label: 'TÃ¼mÃ¼' },
                        { value: '3', label: 'â­ 3 yÄ±ldÄ±z +' },
                        { value: '4', label: 'â­ 4 yÄ±ldÄ±z +' },
                        { value: '5', label: 'â­ 5 yÄ±ldÄ±z' }
                      ].map(option => (
                        <label key={option.value} className="flex items-center gap-3 cursor-pointer group">
                          <input
                            type="radio"
                            name="min_rating"
                            value={option.value}
                            checked={filters.min_rating === option.value}
                            onChange={handleFilterChange}
                            className="w-4 h-4 text-orange-500 cursor-pointer"
                          />
                          <span className="text-sm text-gray-700 group-hover:text-orange-600 transition">{option.label}</span>
                        </label>
                      ))}
                    </div>
                  </div>

                  {/* Reset Filtresi */}
                  <button
                    onClick={() => setFilters({ category: '', district: '', min_rating: '', search: '' })}
                    className="w-full py-2.5 bg-gradient-to-r from-gray-200 to-gray-300 hover:from-gray-300 hover:to-gray-400 text-gray-700 rounded-lg font-medium transition text-sm"
                  >
                    SÄ±fÄ±rla
                  </button>
                </div>
              </div>
            </div>

            {/* Ortada: DÃ¼kkan Grid (2 sÃ¼tun) */}
            <div className="lg:col-span-7">
              {isLoading ? (
                <div className="flex items-center justify-center py-20">
                  <div className="text-center">
                    <div className="w-12 h-12 border-4 border-blue-200 border-t-blue-500 rounded-full animate-spin mx-auto mb-4"></div>
                    <p className="text-gray-600">YÃ¼kleniyor...</p>
                  </div>
                </div>
              ) : workshops.length === 0 ? (
                <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
                  <p className="text-2xl mb-2">ğŸ”</p>
                  <p className="text-gray-600 text-lg">Arama kriterlerine uygun dÃ¼kkÃ¢n bulunamadÄ±.</p>
                  <p className="text-gray-500 text-sm mt-2">Filtrelerinizi deÄŸiÅŸtirmeyi deneyin.</p>
                </div>
              ) : (
                <div className="grid grid-cols-2 gap-6">
                  {workshops.map((ws) => (
                    <Link
                      key={ws.id}
                      to={`/workshops/${ws.id}`}
                      className={`bg-gradient-to-br ${getCategoryColor(ws.category_name)} rounded-2xl shadow-md hover:shadow-2xl transition-all duration-300 overflow-hidden border group flex flex-col h-full`}
                    >
                      {/* Resim AlanÄ± */}
                      <div className="w-full h-40 flex-shrink-0 overflow-hidden">
                        {ws.image ? (
                          <img
                            src={ws.image}
                            alt={ws.name}
                            className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                          />
                        ) : (
                          <div className={`w-full h-full bg-gradient-to-br ${getCategoryColor(ws.category_name)} flex items-center justify-center text-6xl`}>
                            {getCategoryIcon(ws.category_name)}
                          </div>
                        )}
                      </div>

                      {/* Ä°Ã§erik */}
                      <div className="flex-1 flex flex-col justify-between p-5">
                        <div>
                          <div className="flex items-start justify-between gap-2 mb-3">
                            <div className="flex-1">
                              <h3 className="font-bold text-base text-gray-900 group-hover:text-blue-700 transition line-clamp-2">
                                {ws.name}
                              </h3>
                              <p className="text-xs text-gray-600 mt-1 font-medium">{ws.category_name}</p>
                            </div>
                            {ws.is_premium && (
                              <div className="bg-gradient-to-r from-orange-400 to-red-500 text-white px-2 py-1 rounded-full text-xs font-bold whitespace-nowrap">
                                â­
                              </div>
                            )}
                          </div>

                          <p className="text-xs text-gray-700 mb-1 font-medium">ğŸ“ {ws.district}</p>
                          
                          {ws.phone && (
                            <p className="text-xs text-gray-600 line-clamp-1">
                              ğŸ“ {ws.phone}
                            </p>
                          )}
                        </div>

                        {/* Puan ve Yorum GÃ¶stergesi */}
                        <div className="flex items-center justify-between pt-3 border-t border-gray-100 mt-3">
                          <StarRating
                            rating={ws.average_rating || 0}
                            reviews={ws.total_reviews || 0}
                          />
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            {/* SaÄŸ: YardÄ±mcÄ± Panel */}
            <div className="lg:col-span-3 space-y-6">
              {/* Premium Ustalar */}
              <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                <h3 className="font-bold text-lg text-gray-900 mb-4 flex items-center gap-2">
                  <span className="text-2xl">â­</span> Premium Ustalar
                </h3>
                <div className="space-y-3">
                  {workshops
                    .filter(w => w.is_premium)
                    .slice(0, 5)
                    .map(ws => (
                      <Link
                        key={ws.id}
                        to={`/workshops/${ws.id}`}
                        className="block p-3 bg-gradient-to-r from-orange-50 to-red-50 rounded-lg hover:shadow-md transition border border-orange-100"
                      >
                        <div className="flex items-start gap-2">
                          <span className="text-xl">{getCategoryIcon(ws.category_name)}</span>
                          <div className="flex-1 min-w-0">
                            <p className="font-semibold text-sm text-gray-900 truncate">{ws.name}</p>
                            <p className="text-xs text-gray-600">{ws.category_name}</p>
                            <div className="flex gap-1 mt-1">
                              {[...Array(5)].map((_, i) => (
                                <span key={i} className={i < Math.floor(ws.average_rating || 0) ? 'text-yellow-400' : 'text-gray-300'}>
                                  â˜…
                                </span>
                              ))}
                            </div>
                          </div>
                        </div>
                      </Link>
                    ))}
                </div>
              </div>

              {/* PopÃ¼ler Aramalar */}
              <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                <h3 className="font-bold text-lg text-gray-900 mb-4 flex items-center gap-2">
                  <span className="text-2xl">ğŸ”¥</span> PopÃ¼ler Aramalar
                </h3>
                <div className="space-y-2">
                  {['ElektrikÃ§i', 'TesisatÃ§Ä±', 'Marangoz', 'BoyacÄ±', 'Cam UstasÄ±'].map(cat => (
                    <button
                      key={cat}
                      onClick={() => setFilters(prev => ({ ...prev, category: categories.find(c => c.name === cat)?.id || '' }))}
                      className="w-full text-left px-4 py-2 bg-gradient-to-r from-blue-50 to-indigo-50 hover:from-blue-100 hover:to-indigo-100 text-gray-800 rounded-lg transition border border-blue-100 text-sm font-medium"
                    >
                      ğŸ” {cat}
                    </button>
                  ))}
                </div>
              </div>

              {/* Ä°statistikler */}
              <div className="bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl shadow-lg p-6 text-white border border-blue-400">
                <h3 className="font-bold text-lg mb-4">ğŸ“Š Platform Ä°statistikleri</h3>
                <div className="space-y-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-blue-100">Toplam DÃ¼kkÃ¢n:</span>
                    <span className="font-bold">{workshops.length}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-blue-100">Premium Ãœyeler:</span>
                    <span className="font-bold">{workshops.filter(w => w.is_premium).length}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-blue-100">Toplam Yorum:</span>
                    <span className="font-bold">{workshops.reduce((sum, w) => sum + (w.total_reviews || 0), 0)}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default WorkshopListPage;
