import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { inventoryAPI } from '../api';
import { useAuth } from '../hooks';

function PartFormPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const isEdit = !!id;

  const [formData, setFormData] = useState({
    name: '',
    category: '',
    brand: '',
    code: '',
    image: null,
    quantity: 0,
    min_stock: 1,
    current_purchase_price: '',
    current_sale_price: '',
    vehicle_ids: [],
  });

  const [categories, setCategories] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [searchVehicle, setSearchVehicle] = useState('');
  const [filteredVehicles, setFilteredVehicles] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchCategories();
    fetchVehicles();
    if (isEdit) {
      fetchPart();
    }
  }, [id]);

  useEffect(() => {
    if (searchVehicle.trim()) {
      const filtered = vehicles.filter(v => 
        `${v.brand} ${v.model}`.toLowerCase().includes(searchVehicle.toLowerCase())
      );
      setFilteredVehicles(filtered.slice(0, 10));
    } else {
      setFilteredVehicles([]);
    }
  }, [searchVehicle, vehicles]);

  const fetchCategories = async () => {
    try {
      const response = await inventoryAPI.getCategories();
      setCategories(response.data.results || response.data || []);
    } catch (error) {
      console.error('Kategori yükleme hatası:', error);
    }
  };

  const fetchVehicles = async () => {
    try {
      const response = await inventoryAPI.getVehicles();
      setVehicles(response.data.results || response.data || []);
    } catch (error) {
      console.error('Araç yükleme hatası:', error);
    }
  };

  const fetchPart = async () => {
    try {
      setIsLoading(true);
      const response = await inventoryAPI.getPart(id);
      const part = response.data;
      setFormData({
        name: part.name || '',
        category: part.category || '',
        brand: part.brand || '',
        code: part.code || '',
        image: null,
        quantity: part.quantity || 0,
        min_stock: part.min_stock || 1,
        current_purchase_price: part.current_purchase_price || '',
        current_sale_price: part.current_sale_price || '',
        vehicle_ids: part.compatibilities?.map(c => c.vehicle) || [],
      });
    } catch (error) {
      console.error('Parça yükleme hatası:', error);
      setError('Parça yüklenemedi.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === 'file') {
      setFormData({ ...formData, [name]: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleAddVehicle = (vehicleId) => {
    if (!formData.vehicle_ids.includes(vehicleId)) {
      setFormData({
        ...formData,
        vehicle_ids: [...formData.vehicle_ids, vehicleId]
      });
    }
    setSearchVehicle('');
    setFilteredVehicles([]);
  };

  const handleRemoveVehicle = (vehicleId) => {
    setFormData({
      ...formData,
      vehicle_ids: formData.vehicle_ids.filter(id => id !== vehicleId)
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validasyon
    if (!formData.name.trim()) {
      setError('Parça adı zorunludur.');
      return;
    }
    if (!formData.category) {
      setError('Kategori seçimi zorunludur.');
      return;
    }
    if (formData.vehicle_ids.length === 0) {
      setError('En az 1 uyumlu araç seçmelisiniz.');
      return;
    }

    try {
      setIsLoading(true);
      const submitData = new FormData();
      submitData.append('name', formData.name);
      submitData.append('category', formData.category);
      if (formData.brand) submitData.append('brand', formData.brand);
      if (formData.code) submitData.append('code', formData.code);
      if (formData.image) submitData.append('image', formData.image);
      submitData.append('quantity', formData.quantity);
      submitData.append('min_stock', formData.min_stock);
      if (formData.current_purchase_price) submitData.append('current_purchase_price', formData.current_purchase_price);
      if (formData.current_sale_price) submitData.append('current_sale_price', formData.current_sale_price);
      formData.vehicle_ids.forEach(id => submitData.append('vehicle_ids', id));

      if (isEdit) {
        await inventoryAPI.updatePart(id, submitData);
        setSuccess('Parça güncellendi!');
      } else {
        await inventoryAPI.createPart(submitData);
        setSuccess('Parça eklendi!');
      }

      setTimeout(() => {
        navigate('/parts');
      }, 1000);
    } catch (error) {
      console.error('Parça kaydetme hatası:', error);
      setError(error.response?.data?.detail || 'Parça kaydedilemedi.');
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading && isEdit) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-orange-500"></div>
      </div>
    );
  }

  const selectedVehicles = vehicles.filter(v => formData.vehicle_ids.includes(v.id));

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
          <h1 className="text-3xl font-bold text-gray-900 mb-6">
            {isEdit ? '✏️ Parça Düzenle' : '➕ Yeni Parça Ekle'}
          </h1>

          {error && (
            <div className="bg-red-50 text-red-600 p-4 rounded-lg mb-6 border border-red-200">
              {error}
            </div>
          )}

          {success && (
            <div className="bg-green-50 text-green-600 p-4 rounded-lg mb-6 border border-green-200">
              {success}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* ZORUNLU ALANLAR */}
            <div className="bg-orange-50 p-4 rounded-lg border border-orange-200">
              <h2 className="font-bold text-orange-900 mb-4">Zorunlu Alanlar</h2>
              
              {/* Parça Adı */}
              <div className="mb-4">
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Parça Adı (Usta Dili) <span className="text-red-500">*</span>
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Örn: Astra J Fren Balata"
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  required
                />
              </div>

              {/* Kategori */}
              <div className="mb-4">
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Kategori <span className="text-red-500">*</span>
                </label>
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  required
                >
                  <option value="">Kategori Seçin</option>
                  {categories.map(cat => (
                    <option key={cat.id} value={cat.id}>
                      {cat.name}{cat.subcategory ? ` > ${cat.subcategory}` : ''}
                    </option>
                  ))}
                </select>
              </div>

              {/* Araç Uyumluluğu */}
              <div className="mb-4">
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Uyumlu Araçlar <span className="text-red-500">*</span> (En az 1)
                </label>
                
                {/* Araç Arama */}
                <div className="relative mb-3">
                  <input
                    type="text"
                    value={searchVehicle}
                    onChange={(e) => setSearchVehicle(e.target.value)}
                    placeholder="Araç ara (marka, model)..."
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                  {filteredVehicles.length > 0 && (
                    <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto">
                      {filteredVehicles.map(vehicle => (
                        <button
                          key={vehicle.id}
                          type="button"
                          onClick={() => handleAddVehicle(vehicle.id)}
                          className="w-full text-left px-4 py-2 hover:bg-orange-50 border-b border-gray-100 last:border-0"
                        >
                          <div className="font-medium">{vehicle.brand} {vehicle.model}</div>
                          <div className="text-sm text-gray-500">
                            {vehicle.year_from}{vehicle.year_to ? `-${vehicle.year_to}` : ''} 
                            {vehicle.engine && ` • ${vehicle.engine}`}
                          </div>
                        </button>
                      ))}
                    </div>
                  )}
                </div>

                {/* Seçili Araçlar */}
                {selectedVehicles.length > 0 && (
                  <div className="flex flex-wrap gap-2">
                    {selectedVehicles.map(vehicle => (
                      <div
                        key={vehicle.id}
                        className="flex items-center gap-2 bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm"
                      >
                        <span>{vehicle.brand} {vehicle.model} {vehicle.year_from}</span>
                        <button
                          type="button"
                          onClick={() => handleRemoveVehicle(vehicle.id)}
                          className="text-blue-600 hover:text-blue-800 font-bold"
                        >
                          ×
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* OPSİYONEL ALANLAR */}
            <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
              <h2 className="font-bold text-gray-900 mb-4">Opsiyonel Alanlar</h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {/* Marka */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Marka (OEM/Muadil/Üretici)
                  </label>
                  <input
                    type="text"
                    name="brand"
                    value={formData.brand}
                    onChange={handleChange}
                    placeholder="Örn: Bosch, TRW"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                </div>

                {/* Parça Kodu */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Parça Kodu (Opsiyonel)
                  </label>
                  <input
                    type="text"
                    name="code"
                    value={formData.code}
                    onChange={handleChange}
                    placeholder="Sistem otomatik üretecek"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                </div>

                {/* Stok Miktarı */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Stok Miktarı
                  </label>
                  <input
                    type="number"
                    name="quantity"
                    value={formData.quantity}
                    onChange={handleChange}
                    min="0"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                </div>

                {/* Minimum Stok */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Minimum Stok
                  </label>
                  <input
                    type="number"
                    name="min_stock"
                    value={formData.min_stock}
                    onChange={handleChange}
                    min="0"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                </div>

                {/* Alış Fiyatı */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Güncel Alış Fiyatı (TL)
                  </label>
                  <input
                    type="number"
                    name="current_purchase_price"
                    value={formData.current_purchase_price}
                    onChange={handleChange}
                    step="0.01"
                    min="0"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                </div>

                {/* Satış Fiyatı */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Güncel Satış Fiyatı (TL)
                  </label>
                  <input
                    type="number"
                    name="current_sale_price"
                    value={formData.current_sale_price}
                    onChange={handleChange}
                    step="0.01"
                    min="0"
                    className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                  />
                </div>
              </div>

              {/* Görsel */}
              <div className="mt-4">
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Görsel
                </label>
                <input
                  type="file"
                  name="image"
                  onChange={handleChange}
                  accept="image/*"
                  className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none"
                />
              </div>
            </div>

            {/* Butonlar */}
            <div className="flex gap-4 pt-4">
              <button
                type="submit"
                disabled={isLoading}
                className="flex-1 bg-orange-600 hover:bg-orange-700 text-white font-bold py-3 px-6 rounded-lg transition disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isLoading ? 'Kaydediliyor...' : (isEdit ? 'Güncelle' : 'Kaydet')}
              </button>
              <button
                type="button"
                onClick={() => navigate('/parts')}
                className="px-6 py-3 bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold rounded-lg transition"
              >
                İptal
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default PartFormPage;
