import React, { useState, useEffect, useCallback } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { workshopsAPI } from '../api';

function WorkshopListPage() {
  const [workshops, setWorkshops] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchParams] = useSearchParams();
  
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    district: '',
    min_rating: '',
    search: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [categoriesRes, workshopsRes] = await Promise.all([
          workshopsAPI.getCategories(),
          workshopsAPI.getWorkshops(filters)
        ]);
        setCategories(categoriesRes.data.results || categoriesRes.data || []);
        setWorkshops(workshopsRes.data.results || workshopsRes.data || []);
      } catch (error) {
        console.error('Veri yükleme hatası:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchData();
  }, [filters]);

  const handleFilterChange = useCallback((e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  }, []);

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Dükkanlar</h1>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
        {/* Filtreler */}
        <div className="bg-white p-6 rounded-lg shadow h-fit">
          <h2 className="text-lg font-bold mb-4">Filtreler</h2>
          
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Arama</label>
              <input
                type="text"
                name="search"
                value={filters.search}
                onChange={handleFilterChange}
                placeholder="Dükkân adı..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Kategori</label>
              <select
                name="category"
                value={filters.category}
                onChange={handleFilterChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md"
              >
                <option value="">Tümü</option>
                {categories.map(cat => (
                  <option key={cat.id} value={cat.id}>{cat.name}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">İlçe</label>
              <input
                type="text"
                name="district"
                value={filters.district}
                onChange={handleFilterChange}
                placeholder="İlçe adı..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Minimum Puan</label>
              <select
                name="min_rating"
                value={filters.min_rating}
                onChange={handleFilterChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md"
              >
                <option value="">Tümü</option>
                <option value="3">3 yıldız ve üzeri</option>
                <option value="4">4 yıldız ve üzeri</option>
                <option value="5">5 yıldız</option>
              </select>
            </div>
          </div>
        </div>

        {/* Dükkan Listesi */}
        <div className="md:col-span-3">
          {isLoading ? (
            <div className="text-center py-8">Yükleniyor...</div>
          ) : workshops.length === 0 ? (
            <div className="text-center py-8 bg-white rounded-lg">
              <p className="text-gray-600">Arama kriterlerine uygun dükkân bulunamadı.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {workshops.map((ws) => (
                <Link key={ws.id} to={`/workshops/${ws.id}`}
                  className="bg-white p-4 rounded-lg shadow hover:shadow-lg transition flex gap-4">
                  {ws.image && (
                    <img src={ws.image} alt={ws.name} className="w-24 h-24 object-cover rounded" />
                  )}
                  <div className="flex-1">
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-bold text-lg text-gray-800">{ws.name}</h3>
                      <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded text-sm">
                        ★ {ws.average_rating} ({ws.total_reviews} yorum)
                      </span>
                    </div>
                    <p className="text-sm text-gray-600 mb-1">{ws.category_name}</p>
                    <p className="text-sm text-gray-600">{ws.district}</p>
                    <p className="text-sm text-gray-500 mt-2">{ws.phone}</p>
                  </div>
                  {ws.is_premium && (
                    <div className="bg-orange-100 text-orange-800 px-3 py-1 rounded-full text-xs font-bold h-fit">
                      PREMIUM
                    </div>
                  )}
                </Link>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default WorkshopListPage;
